function limitText(elem, limitVal) {
    var text = elem.value;
    if (text.length > limitVal) {
        elem.setAttribute("disabled", "disabled");
        var newText = text.substring(0, limitVal);
        elem.value = newText;
        elem.removeAttribute("disabled");
        elem.focus();
    }
}

